    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        boolean esprimo = true;
        int divisor = 2;
        int num = Integer.valueOf(jTextFieldnumero.getText())/2;
        
        while ((divisor < num ) && esprimo) {
            if (num % divisor == 0) {
                esprimo = false;
                divisor = divisor + 2;
            }
        }
        if (esprimo) {
            jLabeltexto.setText("No es primo");
        } else {
            jLabeltexto.setText("Es primo");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

